import React, { useState } from 'react';
import './App.css';

function App() {
  let [글제목, 글제목변경] = useState(['남자 코트 추천', '라면 추천', '남자 음식 추천']);
  let [따봉, 따봉변경] = useState([0, 0, 0]);
  let [modal, setModal] = useState(false);
  let [글제목제목, 글제목제목변경] = useState();
  let [입력값, 입력값변경] = useState('');
  let [visible, setVisible] = useState([true, true, true]); // 각 요소의 보임/안보임 상태를 배열로 관리

  function 따봉늘리기(index) {
    let copy = [...따봉];
    copy[index] += 1;
    따봉변경(copy);
  }

  // 특정 인덱스의 요소를 숨기는 함수
  function 삭제(index) {
    let copy = [...visible];
    copy[index] = false; // 해당 인덱스의 요소만 숨김
    setVisible(copy);
  }

  return (
    <div className="App">
      <div>
        {글제목.map(function(a, i){
          return visible[i] ? ( // visible 배열을 사용하여 해당 요소가 보여야 하는지 결정
            <div className="list" key={i}>
              <h4 onClick={(e)=>{ e.stopPropagation(); setModal(!modal); 글제목제목변경(글제목[i]) }}>
                { 글제목[i] }<span onClick={(e)=>{ e.stopPropagation(); 따봉늘리기(i) }}>👍</span>{따봉[i]}
              </h4>
              <p>2월 17일 발행</p>
              <button onClick={()=>삭제(i)}>삭제</button>
            </div>
          ) : null;
        })}
      </div>

      {modal ? (
        <div className='modal'>
          <h2>{글제목제목}</h2>
          <p>날짜</p>
          <p>상세 내용</p>
          <button onClick={()=>{setModal(false)}}>닫기</button>
        </div>
      ) : null}
    </div>
  );
}

export default App;
